function ReadEnvs () {
  global $allow_html_code;
  $in = array();
  if (isset($_SERVER['QUERY_STRING'])) {
 	$clp = preg_split("/::/", $_SERVER['QUERY_STRING']);
	
	foreach ($clp as $pair) {
		$name = $value = "";
		list($name, $value) = (preg_split("/==/", $pair));
		$value = urldecode($value);
		$value = preg_replace("/\+/", " ", $value);
		$value = preg_replace("/\|/", " | ", $value);
		$value = preg_replace("/\<\!--.*--\>/", "", $value);
		if ($allow_html_code != 1) {
			$value = preg_replace("/\<.*\>/",'',$value);
		}
		$in[$name] = $value;
	}
 }
 return $in;
}

